import Navbar from "./component/Navbar"
import Card from "./component/Card"
//import FeedSearch from "./component/FeedSearch"
import Search from "./component/Search"
//import data from "./data"
import axios from "axios";

import { useEffect, useState } from "react"
import "primereact/resources/themes/lara-light-indigo/theme.css";  //theme
import "primereact/resources/primereact.min.css";                  //core css
import "primeicons/primeicons.css";                                //icons

export default function App() {
    const [data,setData]= useState([])
    const url="http://localhost:8080/api/Rss_Feed";

    // useEffect(() => {
    //     fetch(url)
    //     .then(response => response.json()).then(json =>{
    //       console.log("jsonnnnnn",json)
    //       setData(json)
    //     }).catch(e=> {
    //       console.log("e",e)
    //     })
    //   },[])

    useEffect(() => {

        fetch(url,{body:JSON.stringify({search:search})})
        .then(response => response.json()).then(json =>{
          console.log("jsonnnnnn",json)
          setData(json)
        }).catch(e=> {
          console.log("e",e)
        })
      },[])


        const [value, setValue] = useState("")

        const sendSearch = async(e)=>{
        const s = e;
        setValue(s);
        const searchApi = await axios.post('/Rss_Feed_route',{search:s});
        console.log(searchApi.data.data);
         SearchData = searchApi.data.data
    }

    const [search, setSearch] = React.useState("")
    console.log(search)

    const cards = SearchData.map(item => {

        return (
            <Card
                key={item._id}
                {...item}
            />

        )

    const desc = item.Description
    //console.log(title)

     if(search ===''){

         return (
             <Card
                 key={item._id}
                 {...item}
             />            )
     }
     if((desc.toLowerCase()).includes(search.toLowerCase()))
         {
             return (
                 <Card
                     key={item._id}
                     {...item}
                 />

             )
         }
    })


    function setString(){
        setSearch(window.localStorage.getItem("search"))
        sendSearch(search)
    }

    window.localStorage.setItem("search","")

    return (
        <div>
            <Navbar /> 
            <Search 
                enter={setString}
            />
             {/* <FeedSearch /> */}
            <section className="cards-list">
                {cards}
            </section>
        </div>
    )
}
