import React from "react"
import Navbar from "./component/Navbar"
import Card from "./component/Card"
//import FeedSearch from "./component/FeedSearch"
import Search from "./component/Search"
//import data from "./data"
import axios from "axios";

import { useEffect, useState } from "react"
import "primereact/resources/themes/lara-light-indigo/theme.css";  //theme
import "primereact/resources/primereact.min.css";                  //core css
import "primeicons/primeicons.css";                                //icons

export default function App() {
    const [data,setData]= useState([])

    const [search, setSearch] = React.useState("")

    const [noOfFeeds, setNoOfFeeds] = useState(0);
    const [loading , setLoading]=useState(false);
    const [endOfList,setEndOfList] = useState(false);


    useEffect(() => {
        async function  loadData() {

        let searchApi = await axios.post('/Rss_Feed_route',{search:search, count:10});
        console.log(searchApi.data.data);
        if(searchApi.data.data.length < 10 ){
            setEndOfList(true);
        }
        setData(searchApi.data.data)
    }
        loadData()
        setNoOfFeeds()
      },[search])

    console.log(search)

    const sendSearch = async()=>{
        //const search = e;
        //setValue(search);
//router.post('/Rss_Feed_route', fetch_rss_feed);
        let searchApi = await axios.post('/Rss_Feed_route',{search:search, count:noOfFeeds+10});
        console.log(searchApi.data.data);
        if(searchApi.data.data.length < 10 ){
            setEndOfList(true);
        }
        else if(searchApi.data.data.length === data.length && searchApi.data.data.length !==10 ){
            setEndOfList(true);
        }

       setData(searchApi.data.data)
       //return searchApi.data.data;
    }

   // const data = sendSearch()

    const seeMore =async ()=>{
        setLoading(true);
        await sendSearch()
        setNoOfFeeds(noOfFeeds+10)
        setLoading(false);

    }
    //const slice = data.slice(0,noOfFeeds)
    const cards = data.map(item => {

        return (
            <Card
                key={item._id}
                {...item}
            />

        )

   // const desc = item.Description
    //console.log(title)    
    })

    // function setString(){
    //     setSearch(window.localStorage.getItem("search"))
    //     //sendSearch(search)
    // }
    // 
    window.localStorage.setItem("search","")

    return (
        <div>
            <Navbar /> 
            <Search 
                enter={setSearch}
            />
             {/* <FeedSearch /> */}
            <section className="cards-list">
                {cards}
            </section>
            { endOfList? null:<button className="seeMore"
            onClick={()=> seeMore()}>{loading? "loading" :"See More"}</button>}
        </div>
    )
}
